import "./RestaurantList.css";
import React, { useState, useEffect } from "react";

const RestaurantList = () => {
  const [restaurantList, setRestaurantList] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch(`http://localhost:5001/restaurants`);
      const data = await response.json();
      setRestaurantList(data);
    };
    fetchData();
  }, []);
  return (
    <>
      <h1>Restaurants</h1>
      <ul>
        {restaurantList.map((restaurant) => {
          return (
            <li key={restaurant.id} className="restaurant-list-grid-container">
              <img
                src={restaurant.image}
                className="restaurant-list-image"
                alt="pictureoffood"
              ></img>
              <p className="restaurant-list-name">{restaurant.name}</p>
              <p className="restaurant-list-description">
                {restaurant.description}
              </p>
              <button>Reserve Now &rarr;</button>
            </li>
          );
        })}
      </ul>
    </>
  );
};

export default RestaurantList;
